import Axios from "axios";
import { API } from '../../constants';
import { useCallback, useState } from "react";
import { CountryText, SearchButton, UniversitiesContainer } from "..";

import "./UniversitiesHandler.css";


export interface IUniversity {
    name: string;
    country: string;
    alpha_two_code: string;
    "state-province": string | null;

    domains: string[];
    web_pages: string[];
}

export function UniversitiesHandler() {
    const [country, setCountry] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [universities, setUniversities] = useState<IUniversity[]>([]);

    const searchButtonHandler = useCallback(() => {
        setIsLoading(true);

        Axios.get(API, {
            params: { country }
        }).then(response => {
            setUniversities(response.data);
        });
    }, [country, isLoading]);

    const countryTextHandler = useCallback((value : string) => {
        console.log('Changed country');
        setCountry(value);
    }, []);

    return (
        <div className="main-container">
            <div className="country-container">
                <CountryText handler={countryTextHandler} />
                <SearchButton handler={searchButtonHandler} />
            </div>
            <UniversitiesContainer universities={universities}/>
        </div>
    );
}
