import { useCallback, useState } from "react";

import "./UniversitiesHandler.css";

import { API } from '../../constants';
import Axios from "axios";

interface IUniversity {
    name: string;
    country: string;
    alpha_two_code: string;
    "state-province": string | null;

    domains: string[];
    web_pages: string[];
}

export function UniversitiesHandler() {
    const [universities, setUniversities] = useState<IUniversity[]>([]);
    const [country, setCountry] = useState("");

    const getUniversities = useCallback(async () => {
        const response = await Axios.get(API, {
            params: { country }
        });

        return response.data;
    }, [country]);

    const searchButtonHandler = useCallback(async () => {
        console.log("Search button pressed!");
        getUniversities().then((universities : IUniversity[]) => {
            setUniversities(universities);
        });
    }, []);

    const countryTextHandler = useCallback((event: {}) => {
        setCountry(event.currentTarget.value);
    }, []);

    return (
        <div className="main-container">
            <div className="country-container">
                <CountryText handler={countryTextHandler} />
                <SearchButton handler={searchButtonHandler} />
            </div>
            <UniversitiesContainer universieties={universities}/>
        </div>
    );
}
