import React from "react";

type Theme = "dark" | "light";

interface IThemeContext {
    theme: Theme;
    setTheme: (theme: Theme) => void;
}

export const ThemeContext = React.createContext<IThemeContext>({
    theme: "light",
    setTheme: (theme: "dark" | "light") => {}
});
