import { useCallback, useState } from "react";

import "./UniversitiesHandler.css";

export function UniversitiesHandler() {
    const [universities, setUniversities] = useState([]);
    const [country, setCountry] = useState("");

    const getUniversities = useCallback(async () => {
        console.log("Getting universities");
    }, [country]);

    const searchButtonHandler = useCallback(async () => {
        console.log("Search button pressed!");
        getUniversities();
    }, []);

    const countryTextHandler = useCallback((event: {}) => {
        console.log(event.currentTarget.value);
    }, []);

    return (
        <div className="main-container">
            <div className="country-container">
                <CountryText handler={countryTextHandler} />
                <SearchButton handler={searchButtonHandler} />
            </div>
            <UniversitiesContainer universieties={universities}/>
        </div>
    );
}
